package com.example.amr.spotifystreamer;import android.app.FragmentManager;import android.app.FragmentTransaction;import android.content.Intent;import android.support.v7.app.ActionBarActivity;import android.os.Bundle;import android.util.DisplayMetrics;import android.view.Display;import android.view.Menu;import android.view.MenuItem;public class MainActivity extends ActionBarActivity {static  int screenWidth;   static int screenHeight;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        DisplayMetrics displaymetrics = new DisplayMetrics();        this.getWindowManager().getDefaultDisplay().getMetrics(displaymetrics);      screenWidth = displaymetrics.widthPixels;        screenHeight = displaymetrics.heightPixels;             setContentView(R.layout.activity_main);        MainActivityFragment mf=new MainActivityFragment();        getSupportFragmentManager().beginTransaction().add(R.id.artistcontain,mf, "Artists Fragment").commit();        getSupportFragmentManager().beginTransaction().add(R.id.trackcontain,new ArtistTopTenFragment(),"Top Ten Tracks").commit();    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {         getMenuInflater().inflate(R.menu.menu_main, menu);        return true;    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        int id = item.getItemId();        if (id == R.id.action_settings) {            return true;        }        if(id==R.id.nowplaying){            Intent local= new Intent(getApplicationContext(),MediaPlayBackService.class);            getApplicationContext().startService(local);        }        return super.onOptionsItemSelected(item);    }}